// Validate Media Folder Link
const mediaFolder = $json['Media Folder Link'];
const platform = $json['Platform'];
const title = $json['Title'] || 'Post';
if (!mediaFolder) {
  return [{
    json: {
      Status: 'Waiting for Media',
      Ready_to_Post: false,
      Additional_Notes: 'Media folder missing',
      RowID: $json['RowID'] || null
    }
  }];
}
return [{json: {mediaFolder, platform, title, RowID: $json['RowID'] || null, mediaFile: $json['Media File']}}];

// Set dynamic FFmpeg output name
const platform = $json['platform'];
const title = $json['title'].replace(/\s+/g, '_');
const isVideo = $json['mediaFile'].endsWith('.mp4') || $json['mediaFile'].endsWith('.mov');
let width, height, aspect;
if (isVideo || platform==='TikTok') { width=1080; height=1920; aspect='9-16'; }
else { width=1080; height=1350; aspect='4-5'; }
const ext = isVideo ? '.mp4' : '.jpg';
const outputName = `${width}x${height}_${aspect}_${platform}_${title}${ext}`;
return [{json:{...$json, FFMPEG_OUTPUT_NAME: outputName}}];

// AI Caption Enhancement
const description = $json['Description'] || '';
const platform = $json['platform'] || 'Instagram';
const manualPost = $json['Status'] === 'Draft';
const enhancedDescription = description + ' #GeneratedByAI';
const hashtags = '#music #live #event';
const usernames = '@stpetemusic';
const status = manualPost ? 'AI Enhanced' : 'AI Generated';
return [{json:{Description: enhancedDescription, Hashtags: hashtags, Usernames: usernames, Status: status, RowID: $json['RowID']}}];

// Extract JSON data from <script> tag
const html = $json['body'];
const match = html.match(/window\._sharedData = (.*);<\/script>/);
if(match){
  const data = JSON.parse(match[1]);
  const posts = data.entry_data.ProfilePage[0].graphql.user.edge_owner_to_timeline_media.edges.map(e => {
    const node = e.node;
    const captionEdge = node.edge_media_to_caption.edges[0];
    const caption = captionEdge ? captionEdge.node.text : '';
    const hashtags = (caption.match(/#\w+/g) || []).join(', ');
    const mentions = (caption.match(/@\w+/g) || []).join(', ');
    return {caption, hashtags, mentions, shortcode: node.shortcode, timestamp: node.taken_at_timestamp};
  });
  return posts.map(p => ({json: p}));
} else { return []; }
